import React from 'react';
import * as Tone from 'tone';

const DTMF_FREQS = {
  '1': [697, 1209], '2': [697, 1336], '3': [697, 1477],
  '4': [770, 1209], '5': [770, 1336], '6': [770, 1477],
  '7': [852, 1209], '8': [852, 1336], '9': [852, 1477],
  '*': [941, 1209], '0': [941, 1336], '#': [941, 1477]
};

function App() {
  const playTone = async (digit) => {
    const [lowFreq, highFreq] = DTMF_FREQS[digit];

    // Create two oscillators
    const lowOsc = new Tone.Oscillator(lowFreq, "sine").toDestination();
    const highOsc = new Tone.Oscillator(highFreq, "sine").toDestination();

    // Create a gain node to control the duration of the tone
    const gain = new Tone.Gain(0.5).toDestination();

    lowOsc.connect(gain);
    highOsc.connect(gain);

    // Start the oscillators
    lowOsc.start();
    highOsc.start();

    // Stop the oscillators after 500ms
    setTimeout(() => {
      lowOsc.stop();
      highOsc.stop();
    }, 500);
  };

  const renderButton = (digit) => (
    <button
      key={digit}
      onMouseDown={() => playTone(digit)}
      style={{ fontSize: '2rem', width: '60px', height: '60px', margin: '5px' }}
    >
      {digit}
    </button>
  );

  return (
    <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', marginTop: '50px' }}>
      <h1>DTMF Dialer</h1>
      <div>
        {['1', '2', '3'].map(renderButton)}
      </div>
      <div>
        {['4', '5', '6'].map(renderButton)}
      </div>
      <div>
        {['7', '8', '9'].map(renderButton)}
      </div>
      <div>
        {['*', '0', '#'].map(renderButton)}
      </div>
    </div>
  );
}

export default App;
