import React, { useState } from 'react';
import * as Tone from 'tone';
import { saveAs } from 'file-saver';

const DTMF_FREQS = {
  '1': [697, 1209], '2': [697, 1336], '3': [697, 1477], 'A': [697, 1633],
  '4': [770, 1209], '5': [770, 1336], '6': [770, 1477], 'B': [770, 1633],
  '7': [852, 1209], '8': [852, 1336], '9': [852, 1477], 'C': [852, 1633],
  '*': [941, 1209], '0': [941, 1336], '#': [941, 1477], 'D': [941, 1633]
};

function App() {
  const [inputValue, setInputValue] = useState('');

  const playTone = async (digit) => {
    const [lowFreq, highFreq] = DTMF_FREQS[digit];

    const lowOsc = new Tone.Oscillator(lowFreq, "sine").toDestination();
    const highOsc = new Tone.Oscillator(highFreq, "sine").toDestination();

    const gain = new Tone.Gain(0.5).toDestination();

    lowOsc.connect(gain);
    highOsc.connect(gain);

    lowOsc.start();
    highOsc.start();

    setTimeout(() => {
      lowOsc.stop();
      highOsc.stop();
    }, 500);
  };

  const handleButtonClick = (digit) => {
    setInputValue(inputValue + digit);
    playTone(digit);
  };

  const generateAudio = () => {
    const sampleRate = 44100;
    const duration = 0.5;
    const numSamples = Math.floor(sampleRate * duration);
    let audioBuffer = new Float32Array(numSamples * inputValue.length);

    inputValue.split('').forEach((digit, i) => {
      const [lowFreq, highFreq] = DTMF_FREQS[digit];
      const t = Array.from({ length: numSamples }, (_, j) => j / sampleRate);
      const lowSignal = t.map(t => Math.sin(2 * Math.PI * lowFreq * t));
      const highSignal = t.map(t => Math.sin(2 * Math.PI * highFreq * t));
      const signal = lowSignal.map((val, j) => (val + highSignal[j]) * 0.5);

      audioBuffer.set(signal, i * numSamples);
    });

    const audioContext = new (window.AudioContext || window.webkitAudioContext)();
    const buffer = audioContext.createBuffer(1, audioBuffer.length, sampleRate);
    buffer.copyToChannel(audioBuffer, 0);

    const wav = audioBufferToWav(buffer);
    saveAs(new Blob([wav], { type: 'audio/wav' }), 'dtmf.wav');
  };

  const audioBufferToWav = (buffer) => {
    const numOfChan = buffer.numberOfChannels,
      length = buffer.length * numOfChan * 2 + 44,
      bufferArray = new ArrayBuffer(length),
      view = new DataView(bufferArray),
      channels = [],
      sampleRate = buffer.sampleRate;

    let offset = 0,
      pos = 0;

    setUint32(0x46464952); // "RIFF"
    setUint32(length - 8); // file length - 8
    setUint32(0x45564157); // "WAVE"

    setUint32(0x20746d66); // "fmt " chunk
    setUint32(16); // length = 16
    setUint16(1); // PCM (uncompressed)
    setUint16(numOfChan);
    setUint32(sampleRate);
    setUint32(sampleRate * 2 * numOfChan); // avg. bytes/sec
    setUint16(numOfChan * 2); // block-align
    setUint16(16); // 16-bit (hardcoded in this demo)

    setUint32(0x61746164); // "data" - chunk
    setUint32(length - pos - 4); // chunk length

    for (let i = 0; i < buffer.numberOfChannels; i++) {
      channels.push(buffer.getChannelData(i));
    }

    while (pos < length) {
      for (let i = 0; i < numOfChan; i++) {
        const sample = Math.max(-1, Math.min(1, channels[i][offset])); // clamp
        view.setInt16(pos, sample < 0 ? sample * 0x8000 : sample * 0x7FFF, true); // convert to 16-bit PCM
        pos += 2;
      }
      offset++;
    }

    return bufferArray;

    function setUint16(data) {
      view.setUint16(pos, data, true);
      pos += 2;
    }

    function setUint32(data) {
      view.setUint32(pos, data, true);
      pos += 4;
    }
  };

  const renderButton = (digit) => (
    <button
      key={digit}
      onMouseDown={() => handleButtonClick(digit)}
      style={{ fontSize: '2rem', width: '60px', height: '60px', margin: '5px' }}
    >
      {digit}
    </button>
  );

  return (
    <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', marginTop: '50px' }}>
      <h1>DTMF Dialer</h1>
      <input
        type="text"
        value={inputValue}
        readOnly
        style={{ fontSize: '2rem', marginBottom: '20px', textAlign: 'center' }}
      />
      <div>
        {['1', '2', '3', 'A'].map(renderButton)}
      </div>
      <div>
        {['4', '5', '6', 'B'].map(renderButton)}
      </div>
      <div>
        {['7', '8', '9', 'C'].map(renderButton)}
      </div>
      <div>
        {['*', '0', '#', 'D'].map(renderButton)}
      </div>
      <button
        onClick={generateAudio}
        style={{ marginTop: '20px', fontSize: '1.5rem', padding: '10px 20px' }}
      >
        Generate Audio
      </button>
    </div>
  );
}

export default App;
